const Body = () => {
    const isMenuOpen=useSelector(store=>store.nav.isMenuOpen);

  return (
    <div className='grid grid-flow-col'>
        {isMenuOpen && <Sidebar/>}
        <MainContainer/>
    </div>
  )
}

export default Body
